mapper.insert:
/* Board - boardInsert */
select spring_board_seq.nextval from dual
INSERT INTO spring_board(b_num, b_name, b_title, b_content, b_pwd) VALUES(#{boardNumber}, #{boardName}, #{boardTitle}, #{boardContent}, #{boardPasswd})

mapper.update:

UPDATE spring_board
SET readcnt = readcnt + 1
WHERE b_num = #{boardNumber}

mapper.select:
/* Board -pwdConfirm */
SELECT NVL((
				SELECT 1 FROM spring_board
				WHERE b_num = #{boardNumber} AND b_pwd = #{boardPasswd}
			), 0) as state
FROM dual

mapper.update:
/* Board - boardUpdate */
UPDATE spring_board
SET b_title = #{boardTitle},
	b_content = #{boardContent},
	b_date = sysdate

		, b_pwd = #{boardPasswd}

	WHERE b_num = #{boardNumber}
